REM first create trigger so we do not have rely on our own
CREATE OR REPLACE TRIGGER ESA.FORM_FIELD_TRIGGER before
	INSERT ON ESA.FORM_FIELD FOR EACH row BEGIN IF inserting THEN IF :NEW."ID" IS NULL THEN
	SELECT FORM_FIELD_SEQ.nextval INTO :NEW."ID" FROM dual;
END IF;
END IF;
END;
/
ALTER TRIGGER ESA.FORM_FIELD_TRIGGER ENABLE;


